INSERT INTO email_templates (name, key, category, sub_category, subject, body, is_active, is_system, created_by, last_modified_by)
VALUES
('Instructor Course Assignment (HTML)', 'course_assigned_instructor_html', 'general', 'default', 'New Course Assignment', '<div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;"><h2 style="color: #007bff;">New Course Assignment</h2><p>You have been assigned to teach a new course:</p><div style="background-color: #f8f9fa; padding: 15px; border-radius: 5px; margin: 15px 0;"><p><strong>Course Name:</strong> {{courseName}}</p><p><strong>Date:</strong> {{date}}</p><p><strong>Time:</strong> {{startTime}} - {{endTime}}</p><p><strong>Location:</strong> {{location}}</p><p><strong>Organization:</strong> {{organization}}</p><p><strong>Number of Students:</strong> {{students}}</p></div><div style="margin: 20px 0; padding: 15px; background-color: #fff3cd; border-radius: 5px;"><p style="margin: 0;"><strong>Important:</strong> Please review these details in your instructor portal and arrive 15 minutes before the class start time.</p></div><p style="color: #6c757d; font-size: 0.9em;">This is an automated message, please do not reply.</p></div>', true, false, NULL, NULL),
('Organization Course Confirmation (HTML)', 'course_scheduled_organization_html', 'general', 'default', 'Course Request Confirmed', '<div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;"><h2 style="color: #007bff;">Course Request Confirmed</h2><p>Your course request has been confirmed and an instructor has been assigned:</p><div style="background-color: #f8f9fa; padding: 15px; border-radius: 5px; margin: 15px 0;"><p><strong>Course Name:</strong> {{courseName}}</p><p><strong>Date:</strong> {{date}}</p><p><strong>Time:</strong> {{startTime}} - {{endTime}}</p><p><strong>Location:</strong> {{location}}</p><p><strong>Instructor:</strong> {{instructorName}}</p><p><strong>Number of Students:</strong> {{students}}</p></div><p>You can view the full details and manage your courses through your organization portal.</p><p style="color: #6c757d; font-size: 0.9em;">This is an automated message, please do not reply.</p></div>', true, false, NULL, NULL); 